import { AnimatePresence, motion, MotionConfig } from "motion/react";
import { ReactNode, useState } from "react";
import { Button, Dialog } from "react-aria-components";
import { twMerge as cn } from "tailwind-merge";
import { MotionModal } from "./motion-component-wrappers";

function Navbar() {
  return (
    <header className="max-w-desktop border-stone sticky inset-x-0 top-0 z-50 mx-auto bg-black p-4">
      <div className="backdrop" />
      <nav>
        {/* TABLET + DESKTOP LAYOUT */}
        <ul className="relative mx-auto flex items-center justify-center gap-x-12 max-sm:hidden lg:gap-x-16 lg:text-[20px]">
          <NavItem href="#">Upcoming</NavItem>
          <NavItem href="#">Legacy</NavItem>
          <li className="w-28 shrink-0 md:w-40 lg:w-52">
            <a href="/" aria-label="Home">
              <Logo />
            </a>
          </li>
          <NavItem href="#">Merch</NavItem>
          <NavItem href="#">About</NavItem>
        </ul>

        {/* MOBILE LAYOUT */}
        <div className="relative flex items-center justify-between sm:hidden">
          <NavMenuModal />
          <a href="/" aria-label="Home">
            <LogoMobile className="text-swapi-400 w-50 overflow-visible" />
          </a>
        </div>
      </nav>
    </header>
  );
}

function NavItem({
  href,
  children,
  className,
}: {
  href: string;
  children: ReactNode;
  className?: string;
}) {
  return (
    <li className={cn("text-lg font-bold", className)}>
      <a href={href}>{children}</a>
    </li>
  );
}

function NavMenuModal() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <MotionConfig transition={{ type: "spring", bounce: 0, duration: 0.45 }}>
      <Button onPress={() => setIsOpen(!isOpen)} aria-label="Hamburger menu">
        <AnimatePresence initial={false}>
          {isOpen ? (
            <CloseIcon className="size-7" />
          ) : (
            <MenuIcon className="size-7" />
          )}
        </AnimatePresence>
      </Button>

      <AnimatePresence>
        {isOpen && (
          <MotionModal
            initial={{ height: 0 }}
            animate={{ height: "auto" }}
            exit={{ height: 0, transition: { duration: 0.2 } }}
            isOpen
            onOpenChange={setIsOpen}
            className="fixed inset-0 top-15 overflow-hidden bg-black"
          >
            <Dialog className="px-4 py-16 text-3xl">
              <motion.ul
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                className="flex flex-col gap-4"
              >
                <NavItem href="#" className="text-3xl">
                  Upcoming
                </NavItem>
                <NavItem href="#" className="text-3xl">
                  Legacy
                </NavItem>
                <NavItem href="#" className="text-3xl">
                  Merch
                </NavItem>
                <NavItem href="#" className="text-3xl">
                  About
                </NavItem>
              </motion.ul>
            </Dialog>
          </MotionModal>
        )}
      </AnimatePresence>
    </MotionConfig>
  );
}

export { Navbar };

// These could probably be moved to a separate file

function CloseIcon({ className }: { className?: string }) {
  return (
    <svg
      className={className}
      viewBox="0 0 24 24"
      xmlns="http://www.w3.org/2000/svg"
    >
      <motion.path
        fill="none"
        stroke="currentColor"
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth="1.5"
        d="M19 5L5 19M5 5l14 14"
        color="currentColor"
        initial={{ pathLength: 0.0 }}
        animate={{
          pathLength: 0.52,
        }}
      />
    </svg>
  );
}

function MenuIcon({ className }: { className?: string }) {
  return (
    <svg
      viewBox="0 0 24 24"
      xmlns="http://www.w3.org/2000/svg"
      className={className}
    >
      <motion.path
        fill="none"
        stroke="currentColor"
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeWidth="1.5"
        d="M10 5h10M4 12h16M4 19h10"
        color="currentColor"
        initial={{ pathLength: 0 }}
        animate={{
          pathLength: 0.45,
        }}
      />
    </svg>
  );
}

